import React from 'react';
import Input from './Input';
import ChoiceButton from './ChoiceButton';
import DifficultySelect from './DifficultySelect';

const InputForm = (props) => {
    const { stepBack, onSubmit, onTyping, newGame, name, room, difficulty } = props;

    if (newGame) {
        return (
            <div className="input-container">
                <Input
                    name='name'
                    placeholder='Your Name...'
                    onChange={onTyping}
                    value={name}
                />
                <DifficultySelect difficulty={difficulty} onTyping={onTyping} /> {/* Pass difficulty and onTyping as props */}
                <div className='nav-container'>
                    <ChoiceButton type='nav-back' choice='back' onChoice={stepBack} label='Back' />
                    <ChoiceButton type='nav-forward' choice='submit' onChoice={onSubmit} label="Let's Go" />
                </div>
            </div>
        );
    } else {
        return (
            <div className="input-container">
                <Input
                    name='name'
                    placeholder='Your Name...'
                    onChange={onTyping}
                    value={name}
                />
                <Input
                    name='room'
                    placeholder='Room ID...'
                    onChange={onTyping}
                    value={room}
                />
                <div className='nav-container'>
                    <ChoiceButton type='nav-back' choice='back' onChoice={stepBack} label='Back' />
                    <ChoiceButton type='nav-forward' choice='submit' onChoice={onSubmit} label="Let's Go" />
                </div>
            </div>
        );
    }
};

export default InputForm;
