import React, { useState, useEffect } from "react";

const CountdownTimer = ({ isActive, onTimeout, turn }) => {
  const [timer, setTimer] = useState(10);

  useEffect(() => {
    let countdownInterval;

    if (isActive && turn) {
      setTimer(10);
      countdownInterval = setInterval(() => {
        setTimer((prevTimer) => {
          if (prevTimer > 0) {
            return prevTimer - 1;
          } else {
            clearInterval(countdownInterval);
            if (onTimeout) {
              onTimeout();
            }
            return 0;
          }
        });
      }, 1000);
    } else {
      // Clear the interval when isActive becomes false or it's not the player's turn
      clearInterval(countdownInterval);
    }

    // Cleanup logic for the interval
    return () => {
      clearInterval(countdownInterval);
    };
  }, [isActive, onTimeout, turn]);

  useEffect(() => {
    // Reset timer when isActive changes to true and it's the player's turn
    if (isActive && turn) {
      setTimer(10);
    }
  }, [isActive, turn]);

  return (
    <div>
      <h1>Countdown Timer: {timer}</h1>
    </div>
  );
};

export default CountdownTimer;
