import React, { useState, useEffect } from "react";
import { Navigate, Link, useLocation } from "react-router-dom";
import io from "socket.io-client";
import Choice from "../components/Choice";
import Loading from "../components/Loading";
import Error from "../components/Error";
import InputForm from "../components/InputForm";

const ENDPOINT = "http://localhost:9000/";
const socket = io(ENDPOINT)

const MainMenu = () => {
  const [state, setState] = useState({
    step: 1,
    name: "",
    newGame: null,
    room: "",
    difficulty: "",
    playerIndex: 0,
    selectedAvatar: "",
    loading: false,
    serverConfirmed: false,
    error: false,
    errorMessage: "",
  });

  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const selectedAvatar = params.get("selectedAvatar");

  useEffect(() => {
    const socket = io(ENDPOINT);

    socket.on("newGameCreated", ({ room, difficulty }) => {
      setState((prevState) => ({
        ...prevState,
        serverConfirmed: true,
        room: room,
      }));
    });

    socket.on("joinConfirmed", ({ difficulty }) => {
      setState((prevState) => ({
        ...prevState,
        serverConfirmed: true,
        difficulty: difficulty,
        playerIndex: 1,
      }));
    });

    socket.on("errorMessage", (message) => displayError(message));

    return () => {
      socket.disconnect();
    };
  }, []);

  const onChoice = (choice) => {
    const gameChoice = choice === "new" ? true : false;
    setState((prevState) => ({
      ...prevState,
      newGame: gameChoice,
    }));
    stepForward();
  };

  const validateNew = () => {
    if (state.newGame) {
      return !(state.name === "" || state.difficulty === "");
    } else {
      return !(state.name === "") && !(state.room === "") && !(state.difficulty === "");
    }
  };

  const validateJoin = () => {
    if (state.newGame) {
      return !(state.name === "");
    } else {
      return !(state.name === "") && !(state.room === "");
    }
  };

  const onSubmit = () => {
    setState((prevState) => ({
      ...prevState,
      loading: true,
    }));

    if (state.newGame && validateNew()) {
      socket.emit("newGame", { difficulty: state.difficulty });
    } else if (state.newGame === false && validateJoin()) {
      socket.emit("joining", { room: state.room });
    } else {
      setTimeout(() => {
        setState((prevState) => ({
          ...prevState,
          loading: false,
        }));
        displayError(state.newGame ? "Please fill out your name and select difficulty" : "Please fill out your name, room id, and select difficulty");
      }, 500);
    }
  };

  const setDifficulty = (diff) => {
    setState((prevState) => ({
      ...prevState,
      difficulty: diff,
    }));
  };

  const stepBack = () => {
    setState((prevState) => ({
      ...prevState,
      step: prevState.step - 1,
    }));
  };

  const stepForward = () => {
    setState((prevState) => ({
      ...prevState,
      step: prevState.step + 1,
    }));
  };

  const onTyping = (e) => {
    const target = e.target.name;
    const value = e.target.value;
    setState((prevState) => ({
      ...prevState,
      [target]: value,
    }));
  };

  const displayError = (message) => {
    setState((prevState) => ({
      ...prevState,
      error: true,
      errorMessage: message,
      loading: false,
    }));
    setTimeout(() => {
      setState((prevState) => ({
        ...prevState,
        error: false,
        errorMessage: "",
      }));
    }, 3000);
  };

  if (state.serverConfirmed) {
    return (
      <Navigate
        to={`/game?room=${state.room}&name=${state.name}&difficulty=${state.difficulty}&playerIndex=${state.playerIndex}&avatar${selectedAvatar}`}
      />
    );
  } else {
    switch (state.step) {
      case 1:
        return (
          <>
            <Choice onChoice={onChoice} />
            <Link to="/leaderboard">
              <button className="btn btn-primary btn-lg btn-block" id="leaderboard-btn">
                LEADERBOARD
              </button>
            </Link>
            <Link to="/avatar">
              <button className="btn btn-primary btn-lg btn-block" id="avatar-btn">
                AVATAR
              </button>
            </Link>
          </>
        );
      case 2:
        return (
          <>
            <Loading loading={state.loading} />
            <Error display={state.error} message={state.errorMessage} />
            <InputForm
              stepBack={stepBack}
              onSubmit={onSubmit}
              onTyping={onTyping.bind(this)}
              newGame={state.newGame}
              name={state.name}
              setDifficulty={setDifficulty}
              room={state.room}
            />
          </>
        );
      default:
        return null;
    }
  }
};

export default MainMenu;
