import React from "react";

class Game extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      game: null,
      firstPlayer: '1',
      turn: true,
      end: false,
      room: '',
      statusMessage: '',
      currentPlayerScore: 0,
      opponentPlayer: [],
      //State to check when a new user join
      waiting: false,
      joinError: false
    }
    this.socketID = null
  }
}

export default Game;
