import { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io.connect('http://localhost:9000');

function App() {

  const [roomNo, setRoomNo] = useState("");
  const [playerName, setPlayerName] = useState("");
  const [playerNameReceived, setPlayerNameReceived] = useState("");
  const [onlineClients, setOnlineClients] = useState(0);

  const sendplayerName = () => {
    socket.emit("sendPlayerName", {playerName, roomNo});
  };

  useEffect(() => {
    socket.on('onlineClientsUpdate', (count) => {
      setOnlineClients(count);
    });

    socket.on('roomFull', (message) => {
      alert(message)
    });

    socket.on("receivePlayerName", (data) => {
      setPlayerNameReceived(data.playerName);
    });
  }, []);

  return (
  <div className='App'>
    <input
      type="text"
      placeholder="Room No."
      onChange={event => setRoomNo(event.target.value)}
    />
    <input
      type="text"
      placeholder="Player Name"
      onChange={event => setPlayerName(event.target.value)}
    />
    <button onClick={sendplayerName}>Start Game</button>
    <h3>Online Clients:</h3>
    {onlineClients}
    <h1>Matched Player:</h1>
    {playerNameReceived}
  </div>
  );

}

export default App;
