import { useState, useEffect } from 'react';
import { usePlayerName } from '../hooks/socketHooks';

export const MainMenu = () => {
  const { playerName, setPlayerName, sendPlayerName } = usePlayerName();
  const [roomNo, setRoomNo] = useState("");
  const [difficulty, setDifficulty] = useState("");

  const handleDifficultyChange = (selectedDifficulty) => {
    setDifficulty(selectedDifficulty);
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Room No."
        onChange={(event) => setRoomNo(event.target.value)}
      />
      <input
        type="text"
        placeholder="Player Name"
        value={playerName}
        onChange={(event) => setPlayerName(event.target.value)}
      />
      <button onClick={() => sendPlayerName(roomNo, playerName, difficulty)}>Start Game</button>
      <div>
        <button onClick={() => handleDifficultyChange("easy")}>Easy</button>
        <button onClick={() => handleDifficultyChange("medium")}>Medium</button>
        <button onClick={() => handleDifficultyChange("hard")}>Hard</button>
      </div>
      <h4>Diff</h4>
    </div>
  );
};
