import React, { useRef } from "react";

const Wait = ({ room, display }) => {
  const textArea = useRef(null);
  const onClick = () => {
    textArea.current.select();
    document.execCommand("copy");
  };

  return (
    <div className="copy">
        <h1 className="copy-message">
          Give your friend the following room id to connect
        </h1>
        <div className="copy-container">
          <div className="room-id-box">
            <label htmlFor="room" className="room-label">Room ID</label>
            <input
                ref={textArea}
                readOnly={true}
                value={room}
                className="copy-area"
                id="room"
            />
  </div>
          <button className="copy-button" onClick={onClick}>
            Copy
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path fill="gray" d="M21 8.94a1.31 1.31 0 0 0-.06-.27v-.09a1.07 1.07 0 0 0-.19-.28l-6-6a1.07 1.07 0 0 0-.28-.19a.32.32 0 0 0-.09 0a.88.88 0 0 0-.33-.11H10a3 3 0 0 0-3 3v1H6a3 3 0 0 0-3 3v10a3 3 0 0 0 3 3h8a3 3 0 0 0 3-3v-1h1a3 3 0 0 0 3-3V8.94Zm-6-3.53L17.59 8H16a1 1 0 0 1-1-1ZM15 19a1 1 0 0 1-1 1H6a1 1 0 0 1-1-1V9a1 1 0 0 1 1-1h1v7a3 3 0 0 0 3 3h5Zm4-4a1 1 0 0 1-1 1h-8a1 1 0 0 1-1-1V5a1 1 0 0 1 1-1h3v3a3 3 0 0 0 3 3h3Z"/></svg>
          </button>
        <h1 className="wait-message">Waiting for player to connect...</h1>

      </div>
    </div>
  );
};

export default Wait;
