import React, { useState, useEffect } from "react";
import { Navigate } from "react-router-dom";
import io from "socket.io-client";
import qs from "qs";

import Wait from "../components/Wait";
import Status from "../components/Status";
import GridEasy from "../components/GridEasy";

const ENDPOINT = "http://localhost:9000/";
const socket = io.connect(ENDPOINT);

const Game = () => {
  const [state, setState] = useState({
    minefield: null,
    grid: null,
    playerIndex: null,
    firstPlayer: null,
    turn: true,
    end: false,
    room: "",
    statusMessage: "",
    currentPlayerScore: 0,
    opponentPlayer: [],
    selectedAvatar: null,
    opponentAvatar: null,
    streakBombs: 0,
    waiting: false,
    joinError: false,
  });

  const setTurn = (turn) => {
    setState({ ...state, turn: state.playerIndex === turn });
  };

  const setGame = (gameState) => {
    setState({ ...state, game: gameState });
  };

  const setMessage = () => {
    const message = state.turn
      ? "Your Turn"
      : `${state.opponentPlayer[0]}'s Turn`;
    setState({ ...state, statusMessage: message });
  };

  const handleClick = (row, col) => {
    const { grid, end, turn, room, playerIndex } = state;
    if (!grid[row][col] && !end && turn) {
      // Handle click logic here
    }
  };

  const handleUpdate = (gameState, turn) => {
    setGame(gameState);
    setTurn(turn);
    setMessage();
  };

  const renderGrid = () => {
    if (state.minefield) {
      return (
        <GridEasy
          minefield={state.minefield}
          handleClick={handleClick}
        />
      );
    }
    return <div></div>;
  };

  useEffect(() => {
    let { room, name, difficulty, playerIndex, avatar } = qs.parse(
      window.location.search,
      {
        ignoreQueryPrefix: true,
      }
    );
    console.log(room, name, difficulty, playerIndex, avatar);
    if (avatar === "undefined") avatar = "avatar1";

    setState({ ...state, room, playerIndex, selectedAvatar: avatar });
    console.log();
    socket.emit("newRoomJoin", {
      room,
      name,
      difficulty,
      avatar,
      playerIndex,
    });

    socket.on("waiting", () =>
      setState({
        ...state,
        waiting: true,
        currentPlayerScore: 0,
        opponentPlayer: [],
      })
    );

    socket.on(
      "starting",
      ({ minefield, gameState, players, turn, avatars }) => {
        setState({
          ...state,
          minefield: minefield,
          waiting: false,
          opponentAvatar: avatars[playerIndex === 0 ? 1 : 0],
        });
        this.gameStart(gameState, players, turn);
        console.log(
          gameState,
          players,
          turn,
          state.selectedAvatar,
          state.opponentAvatar
        );
        console.log(minefield);
      }
    );

    socket.on("joinError", () => setState({ ...state, joinError: true }));

    socket.on("setFirstPlayer", ({ firstPlayer }) => {
      setState({ ...state, firstPlayer });
    });

    socket.on("update", ({ gameState, turn }) =>
      this.handleUpdate(gameState, turn)
    );

    socket.on("winner", ({ gameState, turn }) =>
      this.handleWin(gameState, turn)
    );

    socket.on("restart", ({ gameState, turn }) =>
      this.handleRestart(gameState, turn)
    );

    return () => {
      socket.off("waiting");
      socket.off("starting");
      socket.off("joinError");
      socket.off("setFirstPlayer");
      socket.off("update");
      socket.off("winner");
      socket.off("restart");
    };
  }, [socket]);

  return (
    <>
      <Wait display={state.waiting} room={state.room} />
      <Status message={state.statusMessage} />
      <div>{renderGrid()}</div>
    </>
  );
};

export default Game;
