import React, { Component } from 'react';
import { Navigate } from 'react-router-dom';

import InputForm from '../components/InputForm'
import Choice from '../components/Choice';
import Loading from '../components/Loading';
import Error from '../components/Error';

import io from 'socket.io-client';
const ENDPOINT = 'http://localhost:9000/'

class MainMenu extends Component {
  constructor(props) {
    super(props)
        this.state = {
            step: 1,
            name: '',
            newGame: null,
            room: '',
            difficulty: '',
            loading: false,
            serverConfirmed: false,
            error: false,
            errorMessage: '',
        };
  }

  componentDidMount = () => {
    this.socket = io(ENDPOINT);
    this.socket.on('newGameCreated', (room) =>{
        this.setState({serverConfirmed:true, room:room})
    });
    this.socket.on('joinConfirmed', ()=>{
        this.setState({serverConfirmed:true})
    });
    this.socket.on('errorMessage', (message) => this.displayError(message));
  } 

  componentWillUnmount(){
    this.socket.disconnect()
  }

  onChoice = (choice)=>{
    const gameChoice = choice==='new'?true:false
    const newState = { newGame: gameChoice };
    this.setState(newState, ()=>{
        this.stepForward()
    })
  }

  validate = () => {
    if (this.state.newGame) {
        return !(this.state.name === '' || this.state.difficulty === '');
    } else {
        return !(this.state.name === '') && !(this.state.room === '') && !(this.state.difficulty === '');
    }
  }

  onSubmit = () => {
    this.setState({ loading: true });
    console.log(this.state);
    if (this.validate()) {
        if (this.state.newGame) {
            this.socket.emit('newGame', { difficulty: this.state.difficulty });
        } else {
            this.socket.emit('joining', { room: this.state.room });
        }
    } else {
        setTimeout(() => this.setState({ loading: false }), 500);
        this.displayError(this.state.newGame ? 'Please fill out your name and select difficulty' : 'Please fill out your name, room id, and select difficulty');
    }
  }

  setDifficulty = (diff) => {
    this.setState({difficulty: diff});
  }

  stepBack = ()=>{
    this.setState({step: this.state.step - 1})
  }

  stepForward = () =>{
      this.setState({step: this.state.step + 1})
  }

  onTyping = (e) => {
    const target = e.target.name;
    const value = e.target.value;
    const newState = { [target]: value };
    this.setState(newState);
  }

  displayError = (message) =>{
    this.setState({error:true, errorMessage:message, loading:false})
    setTimeout(()=>{
        this.setState({error:false, errorMessage:''})
    }, 3000)
  }

  render(){
    if (this.state.serverConfirmed){
        return(
          <Navigate to={`/game?room=${this.state.room}&name=${this.state.name}&diff=${this.state.difficulty}`} />
        )
    }else{
        switch(this.state.step){
            case(1):
                return (
                    <Choice onChoice={this.onChoice}/>
                );
            case(2):
                return (
                    <>
                        <Loading loading={this.state.loading}/>
                        <Error display={this.state.error} message={this.state.errorMessage}/>
                        <InputForm 
                            stepBack={this.stepBack} 
                            onSubmit={this.onSubmit} 
                            onTyping={this.onTyping.bind(this)}
                            newGame={this.state.newGame}
                            name = {this.state.name}
                            setDifficulty={this.setDifficulty}
                            room = {this.state.room}/> 
                    </>
                );
            default:
                return null
        }
    }
  }

}

export default MainMenu;
