import { useState, useEffect } from 'react';
import { usePlayerName, useCheckRoom } from '../hooks/socketHooks';
import DifficultyModal from '../components/DifficultyModal';
import axios from 'axios';

export const MainMenu = () => {
  const { playerName, setPlayerName, sendPlayerName } = usePlayerName();
  const [ roomExist, setRoomExist ] = useState(false);
  const [roomNo, setRoomNo] = useState("");
  const [showModal, setShowModal] = useState(false);

  const fetchData = async (roomNoInput) => {
    try {
      const response = await axios.get(`http://localhost:9000/checkRoomExist?roomNo=${roomNoInput}`, { crossdomain: true });
      setRoomExist(response.body.fetchRoomExist ? true : false);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleDifficultySelect = (selectedDifficulty) => {
    setShowModal(true);
    sendPlayerName(roomNo, playerName, selectedDifficulty);
  };

  const handleStartGame = () => {
    fetchData(roomNo);
    if (!roomExist) {
      setShowModal(true);
    } else {
      sendPlayerName(roomNo, playerName, null);
    }
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Room No."
        onChange={(event) => setRoomNo(event.target.value)}
      />
      <input
        type="text"
        placeholder="Player Name"
        value={playerName}
        onChange={(event) => setPlayerName(event.target.value)}
      />
      <button onClick={handleStartGame}>Start Game</button>
      <h4>Room Exist:</h4>
      {showModal && <DifficultyModal onSelectDifficulty={handleDifficultySelect} />}
    </div>
  );
};
