import React from "react";

import Wait from "../components/Wait";
import Status from "../components/Status";

import io from 'socket.io-client';
import qs from 'qs';
import { Navigate } from "react-router-dom";

const ENDPOINT ='http://localhost:9000/'

const {room, name, difficulty} = qs.parse(window.location.search, {
  ignoreQueryPrefix: true
 })

class Game extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      game: null,
      firstPlayer: 0,
      turn: true,
      end: false,
      room: '',
      statusMessage: '',
      currentPlayerScore: 0,
      opponentPlayer: [],
      //State to check when a new user join
      waiting: false,
      joinError: false
    }
    this.socketID = null
  }

  componentDidMount() {
    this.socket = io(ENDPOINT)
    this.setState({room})
    this.socket.emit('newRoomJoin', {room, name, difficulty})
    this.socket.on('waiting', () => this.setState({waiting:true, currentPlayerScore:0, opponentPlayer:[]}))
    //gameState:revealedCells array, players:[[id1, name1], [id2, name2]]
    this.socket.on('starting', ({gameState, players, turn})=> {
      this.setState({waiting:false})
      //this.gameStart(gameState, players, turn)
    })
    this.socket.on('joinError', () => this.setState({joinError: true}))
    this.socket.on('setFirstPlayer', ({startPlayer, id}) => {
      this.setState({startPlayer: startPlayer})
      this.socketID = id 
    })
  }

  render(){
    if (this.state.joinError){
      return(
        <Navigate to={`/`} />
      )
    } else {
      return (
        <>
          <Wait display={this.state.waiting} room={this.state.room}/>
          <Status message={this.state.statusMessage}/>
        </>
      )
    // } 
  }
}
}
export default Game;
